/* table of contents 
---------------------------------------

1. Responsiveness tablet
2. Responsiveness mobile

1 Responsiveness Tablet 1281px 

*/

@media (max-width:1281px) {

    /* 1.1 general resize by changing the font size in html*/
    html {
        font-size: 60%;
    }

    /* 1.2 Default box resizing  */
    .default-box {

        width: 90vw;
        height: 90vh;

        margin: 0rem 10rem;
    }

    /* 1.3 game area */
    /* 1.3.1. memory blocks styling */

    .memory-block-and-start .margin--30 {
        margin: 0rem 25%;
    }

    .memory-block-and-start .margin--20 {
        margin: 0rem 15%;
    }

    .memory-block-and-start .margin--15 {
        margin: 0rem 10%;
    }

    .memory-block-and-start .margin--10 {
        margin: 0rem 5%;
    }

}


/*
2. Responsiveness Tablet 801px 
*/

@media (max-width:801px) {

    /* 2.1 general resize by changing the font size in html*/
    html {
        font-size: 55%;
    }

    /* 2.2 Default box resizing  */
    .default-box {

        width: 92.5vw;
        height: 92.5vh;

    }

    /* 2.3. game area */
    /* 2.3.1 memory blocks styling */

    .memory-block-and-start .margin--30 {
        margin: 0rem 15%;
    }

    .memory-block-and-start .margin--20 {
        margin: 0rem 10%;
    }

    .memory-block-and-start .margin--15 {
        margin: 0rem 10%;
    }

    .memory-block-and-start .margin--10 {
        margin: 0rem 5%;
    }

    /* 2.4. Difficulty select menu */

    .difficulty-select {

        flex-basis: 60%;
        gap: 4rem;

    }



}


/*
3. Responsiveness Mobile 641px 
*/

@media (max-width:585px) {

    /* 3.1 general resize by changing the font size in html*/
    html {
        font-size: 50%;
    }

    /* 3.2 Default box resizing  */
    .default-box {

        width: 95vw;
        height: 95vh;

    }

    /* 3.3. game area */

    /*message text */
    .text-box {
        margin: 0rem 2rem;
    }

    /* 3.3.1 memory blocks styling */
    .memory-block-and-start .margin--30 {
        margin: 0rem 20%;
    }

    .memory-block-and-start .margin--20 {
        margin: 0rem 10%;
    }

    .memory-block-and-start .margin--15 {
        margin: 0rem 7.5%;
    }

    .memory-block-and-start .margin--10 {
        margin: 0rem 5%;
    }

    /* 3.3.2 bottom area */

    /* center elements */
    .bottom-area {
        flex-wrap: wrap;
        justify-content: center;
    }

    .support-quit-button {
        flex-basis: 100%;
        justify-content: center;
    }

    /* switch position scorebox and quit button */
    .support-quit-button {
        order: 2;
    }

    /* 3.4 Difficulty select menu */
    .difficulty-select {

        flex-basis: 50%;
        gap: 6rem;

    }

}
